import React, { useState } from 'react';
// eslint-disable-next-line no-unused-vars
import classes from "./Checkout.module.scss";

const Checkout = ({ cart = [], total, onConfirmOrder }) => {
  const [shippingDetails, setShippingDetails] = useState({
    name: '',
    address: '',
    email: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setShippingDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleQuantityChange = (productId, newQuantity) => {
    // Update the quantity for the specific product in the cart
    // You can implement this based on your state management logic
  };

  return (
    <div>
      <h2>Checkout</h2>
      <div className="checkout">
        <div>
          <h3>Shipping Details</h3>
          <form>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={shippingDetails.name}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Address:
              <input
                type="text"
                name="address"
                value={shippingDetails.address}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Email:
              <input
                type="text"
                name="email"
                value={shippingDetails.email}
                onChange={handleInputChange}
              />
            </label>
          </form>
        </div>
        <div>
          <h3>Order Summary</h3>
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <div className="cart-item-details">
                <img src={item.image} alt={item.name} />
                <div>
                  <p>{item.name}</p>
                  <p>{item.price}</p>
                </div>
              </div>
              <div className="cart-item-actions">
                <label>
                  Quantity:
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                  />
                </label>
                <p>Subtotal: {item.price * item.quantity}</p>
              </div>
            </div>
          ))}
          <p>Total: {total}</p>
        </div>
        <button onClick={() => onConfirmOrder(shippingDetails)}>Confirm Order</button>
      </div>
    </div>
  );
};

export default Checkout;
