import React, { useEffect, useState } from "react";
import useCart from "../../hooks/useCart";
import useProducts from "../../hooks/useProducts";
import {
  addToCart,
  getItemFromLocalStorage,
  removeFromCart,
} from "../../utilities/fakedb";
import Cart from "../Cart/Cart";
import CartItem from "../CartItem/CartItem";
import classes from "./ReviewOrder.module.scss";

const ReviewOrder = ()  => {
  const [products] = useProducts();
  const [cart, setCart] = useCart(products);
  const [displayCart, setDisplayCart] = useState([]);

  const [shippingDetails, setShippingDetails] = useState({
    name: '',
    address: '',
    email: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setShippingDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  useEffect(() => {
    let storedCart = getItemFromLocalStorage("shopping_cart");
    if (storedCart) {
      storedCart = JSON.parse(storedCart);
      const structuredCart = [];
      for (const key in storedCart) {
        const product = products.find((product) => product.key === key);
        if (product) {
          product["quantity"] = storedCart[key];
          structuredCart.push(product);
        }
      }
      setDisplayCart(structuredCart);
    }
  }, [cart, products]);

  const handleIncrease = (productId) => {
    const product = products.find((product) => product.key === productId);
    if (product) {
      product.quantity += 1;
      addToCart(product);
      setCart([...cart, product]);
    }
  };

  const handleDecrease = (productId) => {
    const product = products.find((product) => product.key === productId);
    if (product) {
      const productIndex = cart.indexOf(product);
      const cartProducts = cart; // we can't modify state variables. but we need to splice it.
      cartProducts.splice(productIndex, 1);
      removeFromCart(product);
      setCart([...cartProducts]);
    }
  };

  return (
    <div className={classes.reviewContainer}>
      <h2 className={classes.title}>Cart Items</h2>
      <div className={classes.body}>
        <div className={classes.productContainer}>
          {displayCart.map((product) => (
            <CartItem
              key={Math.random()}
              item={product}
              handleIncrease={handleIncrease}
              handleDecrease={handleDecrease}
            ></CartItem>
          ))}
        </div>
        <div className={classes.cartContainer}>
          <Cart cart={cart} />
        </div>
      </div>
      {/* <div>
      <h2>Checkout</h2>
      <div className="checkout">
        <div>
          <h3>Shipping Details</h3>
          <form>
            <label>
              Name:
              <input
                type="text"
                name="name"
                value={shippingDetails.name}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Address:
              <input
                type="text"
                name="address"
                value={shippingDetails.address}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Email:
              <input
                type="text"
                name="email"
                value={shippingDetails.email}
                onChange={handleInputChange}
              />
            </label>
          </form>
        </div>
        <button>Confirm Order</button>
      </div>
    </div> */}
     <div className={classes.shippingDetailsForm}>
      <h3>Shipping Details</h3>
      <form>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={shippingDetails.name}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Address:
          <input
            type="text"
            name="address"
            value={shippingDetails.address}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={shippingDetails.email}
            onChange={handleInputChange}
          />
        </label>
      </form>
    </div>
    <button>Confirm Order</button>
    </div>
  );
};

export default ReviewOrder;
