import React, { useState } from "react";
import classes from "./Cart.module.scss";
import Checkout from "../Checkout/Checkout";
import { NavLink } from "react-router-dom";

const Cart = (props) => {
  const { cart } = props;
  const [isCheckoutVisible, setIsCheckoutVisible] = useState(false);

  const cartPriceReducer = (prev, product) => prev + product.price;
  const total = cart.reduce(cartPriceReducer, 0);

  const handleCheckoutClick = () => {
    setIsCheckoutVisible(true);
  };

  return (
    <div className={classes.cartContainer}>
      <h3 className={classes.title}>Order Summary</h3>
      <h4>Item Ordered: {cart.length || 0}</h4>
      <h4>Total Price: {total.toFixed(2)}</h4>

      <button className={classes.checkout} onClick={handleCheckoutClick}>
      <NavLink to="/checkout"> Checkout</NavLink>
      </button>

      {isCheckoutVisible && <Checkout cart={cart} total={total} />}
    </div>
  );
};

export default Cart;
